from PyQt5 import QtCore, QtGui, QtWidgets

class Ui_MainWindow(object):
    def calculate_gpa(self):
        try:
            credits = [float(getattr(self, f'lineEdit_credits{i+1}').text() or 0) for i in range(10)]
            grades = [float(getattr(self, f'lineEdit_grades{i+1}').text() or 0) for i in range(10)]

            non_zero_credits = [credit for credit in credits if credit != 0]
            non_zero_grades = [grade for grade in grades if grade != 0]

            if all(credit == 0 or grade == 0 for credit, grade in zip(credits, grades)):
                return QtWidgets.QMessageBox.critical(None,"Error", "You should at least give one credit and one grade!")

            if any(grade > 5 for grade in grades):
                return QtWidgets.QMessageBox.critical(None,"Error", "Maximum grade must be 5!")

            if len(non_zero_credits) != len(non_zero_grades):
                return QtWidgets.QMessageBox.critical(None, "Error", "The amount of credits and grades must be equal!")

            total_points = sum(credit*grade for credit, grade in zip(credits,grades))
            total_credits = sum(credit for credit in credits)

            gpa = total_points/total_credits

            self.lineEdit_GPA.setText(f'{gpa:.2f}')

        except ValueError as e:
            return QtWidgets.QMessageBox.critical(None, "Error", "Please only give integer values!")



